import React from 'react';
import TopBar from './TopBar';
import Grid from './Grid';

var bookSamples = {
  1: {
    name: 'The Scar',
    author: 'China Mieville',
    urlToImg: '',
    read: false,
  },
  2: {
    name: 'Perdido Street Station',
    author: 'China Mieville',
    urlToImg: '',
    read: false,
  }
}


class App extends React.Component{
  constructor(props){
    super(props);
    this.handleFilter = this.handleFilter.bind(this);
  }
  state = {
    books: [],
    filterText: '',
  }

  handleFilter(input){
    this.setState(input)
  }

  render(){
    return (
      <React.Fragment>
      <TopBar />
      <Grid />
      {/* <AddBookForm /> */}
      </React.Fragment>
    )
  }
}

export default App;
