import React from 'react';
import TopBar from './TopBar';
import Grid from './Grid';
import bookSamples from './bookSamples'



class App extends React.Component{
  constructor(props){
    super(props);
    this.handleFilter = this.handleFilter.bind(this);
  }
  state = {
    books: {},
    filterText: '',
  }

  handleFilter(input){
    this.setState(input)
  }

  componentDidMount(){
    this.setState({books: bookSamples})
  }

  render(){
    return (
      <React.Fragment>
      <TopBar />
      <Grid loadSamples={this.loadSamples}/>
      {/* <AddBookForm /> */}
      </React.Fragment>
    )
  }
}

export default App;
