import React from 'react';


class AddBookForm extends React.Component{

  nameRef = React.createRef();
  authorRef = React.createRef();
  linkToImgRef = React.createRef();

  createBook = e => {
    e.preventDefault();
    const book = {
      name : this.nameRef.current.value,
      author : this.authorRef.current.value,
      urlToImg : this.linkToImgRef.current.value,
    }
    this.addBook(book);
  }
  addBook = (book) => {
    console.log(`name: ${book.name}`);
    console.log(`author: ${book.author}`);
    console.log(`urk: ${book.urlToImg}`);

    const books = {...this.props.books};

    // books[`book${Date.now()}`] = book;
    this.props.books =
      books

  }

  render() {
    return (
      <form onSubmit={this.createBook}>
        <input placeholder='name' ref={this.nameRef} type="text"/>
        <input placeholder='author' ref={this.authorRef}type="text"/>
        <input placeholder='image URL' ref={this.linkToImgRef} type="text"/>
        <button type="submit">+ add book</button>
      </form>
    )
  }
}
export default AddBookForm;
